raw.css:
.primo-copy {
	@apply py-0 text-gray-700; 
}

@media print {
	.root {
    	@apply m-0;
    }
    .noprint {
		display: none;
    }
    .container {
    	@apply p-0;
    }
    .print\:p-0 {
    	@apply p-0;
    }
    .print\:shadow-none {
    	@apply shadow-none;
    }
    .print\:link {
    	&:after {
    		content: " (" attr(href) ") ";
        }
    }
}

.disabled {
	@apply opacity-25 bg-red-500 cursor-default;
}

raw.js:
import Alpine from 'alpinejs'

import AOS from 'aos'
AOS.init()

import colordom from 'colordom'

import Glider from 'glider-js'

new Glider(document.querySelector(`#component-${primo.id} .glider-recipe`), {
  slidesToShow: 2,
  draggable: true,
  arrows: {
    prev: `#component-${primo.id} .gallery .prev`,
    next: `#component-${primo.id} .gallery .next`
  }
});

const nodes = document.querySelectorAll('[print]')
nodes.forEach(node => {
    node.addEventListener('click', () => {
    	const target = node.getAttribute('print')
        const head = document.querySelector('head').innerHTML
        const divContents = document.querySelector(target).innerHTML; 
        const a = window.open('', 'PRINT', 'height=400,width=600'); 
        console.log(a)
        a.document.write(`
        <html>
            <head>${head}</head>
            <body >${divContents}</body>
        </html>`);
        a.document.close(); // necessary for IE >= 10
        a.focus(); // necessary for IE >= 10*/
        a.onload = () => {
        	window.print()
            window.close()
        }
    })
})

